#!/usr/bin/env node

/**
 * Packages
 */
const program = require('commander')

/**
 * CSV2HTML
 */
const CSV2HTML = require('../src/')

program
  .version('1.0.0')
  .option('-i, --input <path>', 'CSV input file location')
  .option('-o, --output <path>', 'HTML output file location')
  .option('-d, --delimiter <n>', 'CSV delimiter to use', ',')
  .option('-t, --title <name>', 'HTML document title', false)
  .parse(process.argv)

program.input && program.output ? CSV2HTML.parseGlobal(program) :  console.error('CSV2HTML requires at least input and output file !')