const fs = require('fs');
const parseString = require('xml2js').parseString;
const path = require('path');
const dirtToArr = require('dirtoarray');



const XMLtoJSON = (input , output ) => {
	let dirArr = dirtToArr(input);
	dirArr.forEach((e) => {
	fs.readFile(`./${input}/${e}.xml`, 'utf8' , (err,data) => {
		parseString(data, (error, result) => {
			fs.writeFile(`./${output}/${e}.json`, JSON.stringify(result , null ,2), 'utf8', (err,res)=>{
			})
		})
	}) 
})

};



XMLtoJSON('./input' , './output')


module.exports = XMLtoJSON